mapper.select:


     and order_id like concat('%', #{orderId}, '%')
     and passenger_property like concat('%', #{passengerProperty}, '%')
     and passenger like concat('%', #{passenger}, '%')
     and passenger_sex = #{passengerSex}
     and passenger_phone like concat('%', #{passengerPhone}, '%')
     and flight_number like concat('%', #{flightNumber}, '%')
     and creation_date >=  #{creationDate}
     and creation_date <=  date_sub(#{creationDate},interval -1 day)
     and transport_time >= #{transportTime}
     and transport_time <= date_sub(#{transportTime},interval -1 day)
     and departure like concat('%', #{departure}, '%')
     and intermediate_port like concat('%', #{intermediatePort}, '%')
     and destination like concat('%', #{destination}, '%')
     and car_type like concat('%', #{carType}, '%')
     and driver_information like concat('%', #{driverInformation}, '%')
     and driver_base = #{driverBase}
     and passenger_price = #{passengerPrice}
     and parking_fees = #{parkingFees}
     and toll_fees = #{tollFees}
     and points = #{points}
     and note = #{note}

order by transport_time

mapper.update:
update YDOnlineTaxi.order_information
passenger_property = #{passengerProperty}, passenger = #{passenger}, passenger_sex = #{passengerSex}, passenger_phone = #{passengerPhone}, flight_number = #{flightNumber}, creation_date = #{creationDate}, transport_time = #{transportTime}, departure = #{departure}, intermediate_port = #{intermediatePort}, destination = #{destination}, car_type = #{carType}, driver_information = #{driverInformation}, driver_base = #{driverBase}, passenger_price = #{passengerPrice}, parking_fees = #{parkingFees}, toll_fees = #{tollFees}, points = #{points}, note = #{note}, order_status = #{orderStatus}, expire_time = #{expireTime}, refuse_reason = #{refuseReason}, driver_phone_number = #{driverPhoneNumber},
where order_id = #{orderId}

mapper.select:
select order_status
from YDOnlineTaxi.order_information


        and order_id=#{orderId}




    (
    car_type = #{likeCarType1}
    or car_type = #{likeCarType2}
    or car_type = #{likeCarType3}
    )
    and
    (
    order_status = #{likeOrderStatus}
    and DATE_FORMAT(transport_time,'%Y-%m-%d %H:%i:%s') >= DATE_FORMAT(#{minTransportTime},'%Y-%m-%d %H:%i:%s')
    and DATE_FORMAT(transport_time,'%Y-%m-%d %H:%i:%s') <= DATE_FORMAT(#{maxTransportTime},'%Y-%m-%d %H:%i:%s')
    and note=#{note}
    )

order by transport_time,points desc

